INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2259356', '12', '70707070', '<p>简述意志的特征。</p>', '', '2016年山东省临沂市费县教师招聘考试题第94题', '1', '0');
