INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2296449', '12', '60606060', '<p>简述什么是二方连续，并绘制出二方连续的种基本骨式。</p>', '', '2018年下半年教师资格证考试《初中美术》题第27题', '1', '0');
