INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2296450', '2296450', '12', '', '1', '<p>（1）坚持面向全体学生的教学观。</p><p>（2）积极探索有效教学的方法。</p><p>（3）营造有利于激发学生创新精神的学习氛围。</p><p>（4）多给学生感悟美术作品的机会。</p><p>（5）引导学生关注自然环境和社会生活。</p><p>（6）重视对学生学习方法的研究。</p><p>（7）探索各种生动有趣、适合学生身心发展水平的教学手段。</p><p>（8）培养学生健康乐观的心态和持之以恒的学习精神。</p>', '0');
