INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2296451', '12', '60606060', '<p>简述美术教学中小组合作学习的实施要点。</p>', '', '2018年下半年教师资格证考试《初中美术》题第29题', '1', '0');
DELETE FROM edu_course_exercise_qa WHERE id='2296451';
INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2296451', '2296451', '12', '', '1', '<p>小组合作学习，是学生在小组中一起工作，质量取决于每一个成员的表现，以及相互协作的程度，具有双重功能，既能培养学生的交流、沟通和合作的意识与能力。又帮助学生互相启发，获得对知识理解和方法的掌握。实施要点如下：</p><p>（1）合理地分配小组。合作学习的人数不宜过多，一般以4-6个人组成合作小组为宜；</p><p>（2）明确学习目的和分配任务；</p><p>（3）鼓励学生在合作的过程中，学习与同学沟通、交流，准确而清晰的传达自己的意见，共同解决学习中遇到的困难和问题。</p>', '0');
DELETE FROM edu_course_exercise_comment WHERE id='2296451';
