INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2296452', '12', '60606060', '<p>依据《义务教育美术课程标准(2011年版）》评价建议，如何采用多种评价方式评价美术作业？</p>', '', '2018年下半年教师资格证考试《初中美术》题第30题', '1', '0');
