INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2296602', '12', '60606060', '<p>依据《普通高中美术课程标准（实验）》，简述美术鉴赏的课程目标。</p>', '', '2018年下半年教师资格证考试《高中美术》题第29题', '1', '0');
