INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2296604', '12', '60606060', '<p>在《普通高中美术课程标准（实验）》“评价建议”中，如何通过评价促进教师的成长？</p>', '', '2018年下半年教师资格证考试《高中美术》题第30题', '1', '0');
