INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('2296604', '2296604', '12', '', '1', '<p>（1）建立促进美术教师成长的评价体系。教师应转变在教学评价活动中的角色，能对自己的教学行为进行分析与反思，及时记录教学体会，教学成果以及需要改进之处。</p><p>（2）建立以美术教师自评为主，校长、教师、学生、家长等共同参与的评价制度，使美术教师从多种渠道获得反馈信息，不断改进教学，促进自身不断成长。</p>', '0');
