INSERT INTO edu_course_exercise_qa (id, course_exercise_id, type, question, cando, answer, total_score) VALUES ('229667778617', '229667749613', '12', '<p>（3）说明歌曲调式的特点。（2分）</p>', '0', '<p>（3）其调式特点表现为：①五声音阶的创作本身具有独特的民族性，是中国民间音乐的重要标志；②VII级偏音“变宫”的加入使音乐更具变化，下行六度构唱带有河北方言特色。总之，该调式音乐旋律质朴流畅，一气呵成，具有浓郁民族特色。</p>', '2');
