INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('2296716', '12', '50505050', '<p>依据《普通高中音乐课程标准（实验）》的基本理念，按照下列要求进行教学设计。</p><p>教学对象：选修“演奏”模块的学生</p><p>教学内容：器乐合奏《平安夜》</p><p>要求：</p><p>（1）自选合适的乐器，设定教学目标和重点。（10分)</p><p>（2）针对教学重点设计教学过程，其中至少包括2个课堂提问。（15分)</p><p>（3）针对（2）中的主要环节，写出设计意图。（10分)</p>', '', '2018年下半年教师资格证考试《高中音乐》题（精选）第31题', '1', '0');
