INSERT INTO edu_course_exercise (id, question_type, course_id, content, easy_error, source, checked, is_pk) VALUES ('181750', '11', '41414141', '<p>请认真阅读下列材料，并按要求作答。</p><p>       在进行“三角形面积”教学时，指导面积计算公式一般采用两种方法：一种是把两个完全一样的三角形拼成一个平行四边形(见图1)，另一个是利用三角形中位线剪拼成平行四边形(见图2-1)或折叠成长方形(见图2-2)。</p><p><img width="314px" height="175px" src="https://fb.fbstatic.cn/api/tarzan/images/15225d5ec9ddcf2.png?width=700" /></p>', '', '2013年上半年教师资格证考试《教育教学知识与能力》（小学）题第27题', '1', '0');
